import React, { useEffect, useState } from 'react';

const App = () => {
  const [videoData, setVideoData] = useState(null);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const apiUrl = 'https://api-candidate.ogruposix.com/checkout/95BD9233-8FDC-48AD-B4C5-E5BAF7578C15';
    const userToken = '32CDF13C-8FA2-432D-86CA-AD173D523F53';

    const fetchData = async () => {
      try {
        const response = await fetch(apiUrl, {
          headers: {
            'user-token': userToken,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setVideoData({
            headline: data.video_headline,
            subHeadline: data.video_sub_headline,
            url: data.video_url,
          });
          setConnected(true);
        } else {
          setConnected(false);
        }
      } catch (error) {
        console.error('Error connecting to API:', error);
        setConnected(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {connected ? (
        <div>
          <p>Conectado à API com sucesso!</p>
          {videoData && (
            <div>
              <h2>{videoData.headline}</h2>
              <h3>{videoData.subHeadline}</h3>
              <video controls width="600" height="400">
                <source src={videoData.url} type="video/mp4" />
                Seu navegador não suporta o elemento de vídeo.
              </video>
            </div>
          )}
        </div>
      ) : (
        <p>Falha ao conectar à API. Verifique sua conexão ou parâmetros de autenticação.</p>
      )}
    </div>
  );
};

export default App;
