import React, { useState } from 'react';

const FormModal = ({ product, onClose, onSubmit, formData }) => {
  const [formFields, setFormFields] = useState({
    name: '',
    email: '',
    phone_number: '',
    street_number: '',
    street: '',
    district: '',
    city: '',
    state: '',
    // Adicione outros campos conforme necessário
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormFields({
      ...formFields,
      [name]: value,
    });
  };

  const handleSubmit = () => {
    onSubmit(formFields, product.product_id);
    onClose();
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Seu Pedido</h2>
        <h3 className='txtprt'>{product.name}</h3>
        <form className='container-form'>
          {/* Campos do formulário */}
          <div className="mb-3">
            <label htmlFor="name" className="form-label">
              Nome:
            </label>
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
              value={formFields.name || ''}
              onChange={handleChange}
              placeholder="Nome"
            />
          </div>

          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email:
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={formFields.email || ''}
              onChange={handleChange}
              placeholder="Email"
            />
          </div>

          <div className="mb-3">
            <label htmlFor="phone_number" className="form-label">
              Telefone:
            </label>
            <input
              type="tel"
              className="form-control"
              id="phone_number"
              name="phone_number"
              value={formFields.phone_number || ''}
              onChange={handleChange}
              placeholder="Telefone"
            />
          </div>

          <div className="mb-3">
            <label htmlFor="street_number" className="form-label">
              Número:
            </label>
            <input
              type="text"
              className="form-control"
              id="street_number"
              name="street_number"
              value={formFields.street_number || ''}
              onChange={handleChange}
              placeholder="Número"
            />
          </div>

          {/* Adicione classes Bootstrap aos outros campos conforme necessário */}

          <button type="button" className="btn btn-primary" onClick={handleSubmit}>
            Confirmar
          </button>
          <button type="button" className="btn btn-secondary" onClick={onClose}>
            Fechar
          </button>
        </form>
      </div>
    </div>
  );
};

export default FormModal;
