import React, { useEffect, useState } from 'react';

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const apiUrl = 'https://api-candidate.ogruposix.com/checkout/95BD9233-8FDC-48AD-B4C5-E5BAF7578C15';
    const userToken = '32CDF13C-8FA2-432D-86CA-AD173D523F53';

    const fetchData = async () => {
      try {
        const response = await fetch(apiUrl, {
          headers: {
            'user-token': userToken,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setApiData(data);
          setConnected(true);
        } else {
          setConnected(false);
        }
      } catch (error) {
        console.error('Error connecting to API:', error);
        setConnected(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {connected ? (
        <div>
          {apiData && (
            <div>
              <h2>{apiData.object[0].video_headline}</h2>
              <h3>{apiData.object[0].video_sub_headline}</h3>
              <h4>{apiData.object[0].video_url}</h4>


              <video controls width="600" height="400" autoPlay>
                <source src={apiData.object[0].video_url.replace('https://', 'http://')} type="video/mp4" />
                Seu navegador não suporta o elemento de vídeo.
              </video>

              <iframe title="Vídeo" width="560" height="315" src={apiData.object[0].video_url.replace('https://', 'http://')} allowFullScreen ></iframe>
            </div>
          )}
        </div>
      ) : (
        <p>Falha ao conectar à API. Verifique sua conexão ou parâmetros de autenticação.</p>
      )}
    </div>
  );
};

export default App;
