import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard';
import BuyFormModal from './FormModal';

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [connected, setConnected] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedProductIndex, setSelectedProductIndex] = useState(null);

  useEffect(() => {
    const apiUrl = 'https://api-candidate.ogruposix.com/checkout/95BD9233-8FDC-48AD-B4C5-E5BAF7578C15';
    const userToken = '32CDF13C-8FA2-432D-86CA-AD173D523F53';

    const fetchData = async () => {
      try {
        const response = await fetch(apiUrl, {
          headers: {
            'user-token': userToken,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setApiData(data);
          setConnected(true);
        } else {
          setConnected(false);
        }
      } catch (error) {
        console.error('Error connecting to API:', error);
        setConnected(false);
      }
    };

    fetchData();
  }, []);

  const openModal = (selectedIndex, selectedProduct) => {
    setSelectedProductIndex(selectedIndex);
    setSelectedProduct(selectedProduct);  
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setSelectedProductIndex(null);
    setIsModalOpen(false);
  };

  const handleSubmitForm = async (formData, productId) => {
    try {
      const response = await fetch(`https://api-candidate.ogruposix.com/buy/${productId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        console.log('Formulário enviado com sucesso!');
        // Adicione lógica adicional para lidar com o sucesso, se necessário
      } else {
        console.error('Erro ao enviar o formulário:', response.status, response.statusText);
        // Adicione lógica adicional para lidar com o erro na resposta da API
      }
    } catch (error) {
      console.error('Erro na requisição para a API:', error);
      // Adicione lógica adicional para lidar com erro na requisição
    } finally {
      closeModal(); // Fechar o modal independentemente do resultado da requisição
    }
  };

  return (
    <div>
      {connected ? (
        <div>
          {apiData && (
            <div>
              <div className="topo">
                <h1>{apiData.object[0].video_headline}</h1>

                <div className="video-container">
                  <iframe
                    className='video mx-auto'
                    src={apiData.object[0].video_url.replace("https://youtu.be/", "https://youtube.com/embed/") + '?autoplay=1'}
                    frameborder="0"
                    allow="autoplay;"
                  ></iframe>
                </div>

                <h3>{apiData.object[0].video_sub_headline}</h3>
              </div>

              <div className='bgbco'>
                <h2>Nossos Produtos:</h2>
                <div className='card-container'>
                  {apiData.object[0].products.map((product, index) => (
                    <ProductCard
                      key={product.product_id}
                      product={product}
                      index={index}
                      handleBuyClick={openModal}
                    />
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      ) : (
        <p>Falha ao conectar à API. Verifique sua conexão ou parâmetros de autenticação.</p>
      )}

      {isModalOpen && selectedProductIndex !== null && (
        <BuyFormModal
          product={selectedProduct}
          onClose={closeModal}
          onSubmit={handleSubmitForm}
        />
      )}

    </div>
  );
};

export default App;
