import React from 'react';

const ProductCard = ({ product }) => {
  const renderOldPrice = () => {
    if (product.discount > 0) {
      const oldPrice = product.price;
      return <div className="u-old-price">R$ {oldPrice.toFixed(2)}</div>;
    }
    return null;
  };

  return (
    <div key={product.product_id} className="" data-product-id={product.product_id}>
      <div > 
          <img   className="card_img" src={product.image_url} /> 
        
        {product.best_choice && (
          <span name="melhor_compra" className="u-svg-link">
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" background-color="red" class="bi bi-heart" viewBox="0 0 16 16">
          <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.834 3.989 6.286 6.357 3.452-2.368 5.365-4.542 6.286-6.357.955-1.886.838-3.362.314-4.385C13.486.878 10.4.28 8.717 2.01zM8 15C-7.333 4.868 3.279-3.04 7.824 1.143q.09.083.176.171a3 3 0 0 1 .176-.17C12.72-3.042 23.333 4.867 8 15"/>
        </svg>
          </span>
        )}

        <h4 >{product.name}</h4>

        <div className=" ">
          <div className=" ">
            {renderOldPrice()}

            <div className=" ">
            R$ {(product.discount > 0 ? product.price - product.discount : product.price).toFixed(2)}
            </div>

          </div>
        </div>

        <a href={`form.html?`} className="btn btn-primary">Comprar Agora</a>
      </div>
    </div>
  );
};

export default ProductCard;
