import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          'https://api-candidate.ogruposix.com/checkout/95BD9233-8FDC-48AD-B4C5-E5BAF7578C15',
          {
            headers: {
              Authorization: '32CDF13C-8FA2-432D-86CA-AD173D523F53',
            },
          }
        );

        // Extrai a lista de produtos da resposta
        const productList = response.data.products;

        // Atualiza o estado com a lista de produtos
        setProducts(productList);
      } catch (error) {
        console.error('Erro ao obter a lista de produtos:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>Lista de Produtos:</h1>
      <ul>
        {products.map((product) => (
          <li key={product.id}>{product.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default ProductList;
