import React from 'react';
import { useNavigate } from 'react-router-dom';

const PedidoEnviado = () => {
  const navigate = useNavigate();

  const handleVoltarParaPaginaInicial = () => {
    navigate('/');
  };

  return (
    <div className="centered-content">
      <h1>Obrigado por sua compra!</h1>
      <p>Seu pedido foi enviado com sucesso.</p>
      <button className="btn btn-primary" onClick={handleVoltarParaPaginaInicial}>Voltar para a PÃ¡gina Inicial</button>
    </div>
  );
};

export default PedidoEnviado;
