import React from 'react';
import { useHistory } from 'react-router-dom';

const PedidoEnviado = () => {
  const history = useHistory();

  const handleVoltarParaPaginaInicial = () => {
    // Usando uma barra (/) como caminho para a página inicial
    history.push('/');
  };

  return (
    <div class="centered-content">
      <h1>Pedido Enviado</h1>
      <p>Obrigado por sua compra! Seu pedido foi enviado com sucesso.</p>
      <button className="btn btn-primary" onclick="window.location.href='pagina-inicial.html'">Voltar para a Página Inicial</button>
    </div>

  );
};

export default PedidoEnviado;
