import React from 'react';


const App = () => {
  const [apiData, setApiData] = useState(null);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const apiUrl = 'https://api-candidate.ogruposix.com/checkout/95BD9233-8FDC-48AD-B4C5-E5BAF7578C15';
    const userToken = '32CDF13C-8FA2-432D-86CA-AD173D523F53';

    const fetchData = async () => {
      try {
        const response = await fetch(apiUrl, {
          headers: {
            'user-token': userToken,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setApiData(data);
          setConnected(true);
        } else {
          setConnected(false);
        }
      } catch (error) {
        console.error('Error connecting to API:', error);
        setConnected(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {connected ? (
        <div>
          {apiData && (
            <div>
              <h1>{apiData.object[0].video_headline}</h1>
              <h3>{apiData.object[0].video_sub_headline}</h3>
              <h4>{apiData.object[0].video_url}</h4> 

              <iframe title="Vídeo" width="560" height="315" src='https://www.youtube.com/watch?v=T5W3TJtYa2E&' allowFullScreen ></iframe>

                  {/* Exibir os produtos */}
    <div>
      <h3>Produtos:</h3>
      {apiData.object[0].products.map((product) => (
        <div key={product.product_id}>
          <p>{product.name}</p>
          <p>Preço: {product.price}</p>
          {/* Adicione mais informações do produto conforme necessário */}
          <img src={product.image_url} alt={product.name} />
        </div>
      ))}
    </div>

              <pre>{JSON.stringify(apiData, null, 2)}</pre>

            </div>

          )}
        </div>
      ) : (
        <p>Falha ao conectar à API. Verifique sua conexão ou parâmetros de autenticação.</p>
      )}
    </div>
  );
};

export default App;
