import React, { useEffect, useState } from 'react';
import ReactPlayer from 'react-player';
import ProductCard from './ProductCard'; // Importe o componente ProductCard

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const apiUrl = 'https://api-candidate.ogruposix.com/checkout/95BD9233-8FDC-48AD-B4C5-E5BAF7578C15';
    const userToken = '32CDF13C-8FA2-432D-86CA-AD173D523F53';

    const fetchData = async () => {
      try {
        const response = await fetch(apiUrl, {
          headers: {
            'user-token': userToken,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setApiData(data);
          setConnected(true);
        } else {
          setConnected(false);
        }
      } catch (error) {
        console.error('Error connecting to API:', error);
        setConnected(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div >
      {connected ? (
        <div>
          {apiData && (
            <div >
              <div className="topo" >
              <h1>{apiData.object[0].video_headline}</h1>

              <div className="video-container">
                <iframe className='video mx-auto' src={apiData.object[0].video_url.replace("https://youtu.be/", "https://youtube.com/embed/")}  width="695" height="391" frameborder="0" allow="autoplay;" ></iframe>
              </div>

              <h3>{apiData.object[0].video_sub_headline}</h3>
              </div>

              <div className='bgbco'>
                <h2>Nossos Produtos:</h2>
                <div  className='card-container'>
                  {apiData.object[0].products.map((product) => (
                    <ProductCard key={product.product_id} product={product} />
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      ) : (
        <p>Falha ao conectar à API. Verifique sua conexão ou parâmetros de autenticação.</p>
      )}
    </div>
  );
};

export default App;
