import React from 'react';

const ProductCard = ({ product }) => {
  const renderOldPrice = () => {
    if (product.discount > 0) {
      const oldPrice = product.price;
      return <div className="u-old-price" style={{ textDecoration: 'line-through !important' }}>${oldPrice}</div>;
    }
    return null;
  };

  return (
    <div key={product.product_id} className="u-align-left u-container-style u-products-item u-repeater-item u-white u-repeater-item-3" data-product-id={product.product_id}>
      <div className="u-container-layout u-similar-container u-container-layout-3">
        <a className="u-product-title-link" href={`products/${product.url}`}>
          <img alt="" className="u-expanded-width u-image u-image-contain u-image-default u-product-control u-image-3" src={product.image_url} />
        </a>
        
        {product.best_choice && (
          <span name="melhor_compra" className="u-svg-link"><i class="bi bi-heart-fill"></i>
          </span>
        )}

        <h4 className="u-align-center u-product-control u-text u-text-default u-text-3">
          <a className="u-product-title-link" href={`products/${product.url}`}>{product.name}</a>
        </h4>

        <div className="u-align-center u-product-control u-product-price u-product-price-3">
          <div className="u-price-wrapper u-spacing-10">
            {renderOldPrice()}

            <div className="u-price u-text-palette-1-base" style={{ fontSize: '1.5rem', fontWeight: 700 }}>
              ${product.discount > 0 ? product.price - product.discount : product.price}
            </div>
          </div>
        </div>

        <a href={`form.html?`} className="btn btn-primary">Comprar Agora</a>
      </div>
    </div>
  );
};

export default ProductCard;
