import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          'https://api-candidate.ogruposix.com/checkout/32CDF13C-8FA2-432D-86CA-AD173D523F535',
          {
            headers: {
              'user-token': 'Hugo - 32CDF13C-8FA2-432D-86CA-AD173D523F53',
            },
          }
        );

        // Extrai a lista de produtos da resposta
        const productList = response.data.products;

        // Atualiza o estado com a lista de produtos
        setProducts(productList);
      } catch (error) {
        console.error('Erro ao obter a lista de produtos:', error);
      }
    };

    fetchData();
  }, []);


return (
  <div>
    <h1>Lista de Produtos</h1>
    <ul>
      {Array.isArray(products) ? (
        products.map((product) => (
          <li key={product.id}>{product.name}</li>
        ))
      ) : (
        <li>Nenhum produto dispon√≠vel</li>
      )}
    </ul>
  </div>
);

};

export default ProductList;
